import React from "react";
import { ShoppingCart } from "lucide-react";

const products = [
  {
    id: 1,
    name: "Floral Summer Dress",
    price: 1199,
    image: "data:image/jpeg;base64,",
    sizes: ["XS", "S", "M", "L"],
  },
  {
    id: 2,
    name: "Denim Skirt",
    price: 899,
    image: "https://images.unsplash.com/photo-1583496661160-fb5886a0aaaa?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80",
    sizes: ["S", "M", "L"],
  },
  {
    id: 3,
    name: "Butterfly Print Top",
    price: 849,
    image: "data:image/jpeg;base64,",
    sizes: ["S", "M", "L", "XL"],
  },
  {
    id: 4,
    name: "Pink Sneakers",
    price: 1499,
    image: "https://images.unsplash.com/photo-1595950653106-6c9ebd614d3a?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80",
    sizes: ["1", "2", "3", "4", "5"],
  },
  {
    id: 5,
    name: "Unicorn Backpack",
    price: 599,
    image: "data:image/jpeg;base64,",
    sizes: ["One Size"],
  },
  {
    id: 6,
    name: "Sparkly Hair Clips Set",
    price: 149,
    image: "data:image/jpeg;base64,",
    sizes: ["One Size"],
  },
  {
    id: 7,
    name: "Stylish Kurta",
    price: 899,
    image: "https://cdn.shopify.com/s/files/1/0341/4805/7228/files/img_03_04240003-304b-4eb0-9c70-c4e12dcb941c.webp?v=1720521674",
    sizes: ["S", "M", "L"],
  },
  {
    id: 8,
    name: "Ballet Flats",
    price: 1399,
    image: "https://images.unsplash.com/photo-1543163521-1bf539c55dd2?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80",
    sizes: ["1", "2", "3", "4", "5"],
  },
];

const Girls = () => {
  const [selectedSizes, setSelectedSizes] = React.useState<{ [key: number]: string }>({});

  const handleSizeSelect = (productId: number, size: string) => {
    setSelectedSizes(prev => ({
      ...prev,
      [productId]: size
    }));
  };

  const handleAddToCart = (productId: number) => {
    if (!selectedSizes[productId]) {
      alert("Please select a size first");
      return;
    }
    alert(`Added to cart! Size: ${selectedSizes[productId]}`);
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Girls' Collection</h1>
        <div className="flex gap-4">
          <select className="border rounded-md px-3 py-2">
            <option>Sort by</option>
            <option>Price: Low to High</option>
            <option>Price: High to Low</option>
            <option>Newest</option>
          </select>
          <select className="border rounded-md px-3 py-2">
            <option>Filter</option>
            <option>Dresses</option>
            <option>Tops</option>
            <option>Bottoms</option>
            <option>Accessories</option>
          </select>
        </div>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
        {products.map((product) => (
          <div key={product.id} className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow">
            <div className="aspect-w-1 aspect-h-1">
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-[300px] object-cover"
              />
            </div>
            <div className="p-4">
              <h3 className="text-lg font-semibold text-gray-900">{product.name}</h3>
              <p className="text-xl font-bold text-purple-600 mt-2">{product.price}</p>
              
              <div className="mt-4">
                <p className="text-sm text-gray-600 mb-2">Select Size:</p>
                <div className="flex flex-wrap gap-2">
                  {product.sizes.map((size) => (
                    <button
                      key={size}
                      onClick={() => handleSizeSelect(product.id, size)}
                      className={`px-3 py-1 rounded-md text-sm ${
                        selectedSizes[product.id] === size
                          ? 'bg-purple-600 text-white'
                          : 'bg-gray-100 text-gray-800 hover:bg-gray-200'
                      }`}
                    >
                      {size}
                    </button>
                  ))}
                </div>
              </div>

              <button
                onClick={() => handleAddToCart(product.id)}
                className="mt-4 w-full bg-purple-600 text-white py-2 rounded-md hover:bg-purple-700 transition-colors flex items-center justify-center gap-2"
              >
                <ShoppingCart size={20} />
                Add to Cart
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Girls;